<?php/** * Модель данных. Интернет-приемная. Обращения * */class Callback_Model extends Model  {	public $table_name = 'callback';	public function info_cat(){		$this->db			->join('callback_cat', 'callback_cat.id', 'self.cat', 'LEFT')			->select(Array('cat_id'=>'callback_cat.id', 'cat_name' => 'callback_cat.name'));	}	/**	 * Загрузка используемых категорий обращений	 *	 * @return array	 */    function get_cat(){    	$query = $this->db    		->select(Array('cat.id', 'cat.name'))        	->from($this->table_name)        	->left_join(Array('ml_cat' => 'callback_cat'), 'cat.id', 'self.cat')        	->group_by('cat.name')        	->order_by('cat.name');        return $query->get()->rows();    }}