<?php/** * Модель данных. ACL. Логи * */class AuthLogs_Model extends Model  {	public $table_name = 'auth_logs';	public function info_user(){		$this->db			->left_join('auth_users', 'auth_users.id', 'self.user')			->select(Array('user_id' => 'auth_users.id', 'user_fio' => 'auth_users.fio', 'user_username' => 'auth_users.username'));	}    function get_users(){    	$query = $this->db    		->select(Array('id' => 'self.id', 'name' => db::expr("concat(`ml_self`.`fio`, ' [', `ml_self`.`username`, ']')")))	        ->from(Array('ml_self' => 'auth_users'))        	->order_by('self.fio');        return $query->get()->rows();    }}